Method.Implementation:
set queryBase = ""
#dim sc As %Status = $$$OK
if ##class(%Dictionary.ParameterDefinition).IDKEYExists($classname(), $ZCVT(queryType, "U")) {
	/// we want only parameters defined in this class
	set queryBase = $parameter(, $ZCVT(queryType, "U"))
} elseif 0 {
	/// TODO Some other type of "get stuff between SELECT and FROM"
	/// some callback class maybe
} else {
	set sc = $$$ERROR($$$GeneralError, "Query type " _ queryType _ " not found")		
}

if (queryBase'="") {
	set queryBase = "SELECT " _ queryBase _ " FROM "
}

return sc
